import React, {useState} from 'react';
import './App.css';
import axios from "axios";
import { FaTwitter } from "@react-icons/all-files/fa/FaTwitter";



function App() {
  const [text, setText] = useState('');
  const [author, setAuthor] = useState('');

  const GetPosts = async () => {
    try {
      const userPosts = await axios.get(`https://goquotes-api.herokuapp.com/api/v1/random?count=1`);
      console.log(userPosts)
      userPosts.data.quotes.map(quote => {
        setText(quote.text);
        setAuthor(quote.author);
      })

    } catch (err) {
      console.error(err.message);
    }
  };

  const answer = GetPosts()


  return (
    <div id="quote-box" className="quote-box">
      <div>
        <h2 id="text" className="text">{text}</h2>
        <h4 id="author" className="author">- {author}</h4>
      </div>
      <div className='footer'>
        <button className="tweet"> 
          <a className="twitter" href="twitter.com/intent/tweet" target='/'><FaTwitter /></a>
        </button>
        <button id="new-quote" className="new-quote"><IoReloadOutline /></button>
      </div>
    </div>
  );
}

export default App;
